import window from "../../mixins/window";
export default {
  name: "LinkManagement",
  mixins: [window],
  data() {
    return {
      columns: [
        {
          title: "ID",
          key: "id"
        },

        {
          title: "注册链接",
          key: "linkUrl",
          align: "center",
          width: 300
        },
        {
          title: "链接类型",
          align: "center",
          key: "linkTypeStr"
        },
        {
          title: "注册人数",
          align: "center",
          key: "regNum"
        },
        {
          title: "操作",
          align: "center",
          render: (h, params) => {
            if (params.row.linkType == 2) {
              return h("div", [
                h(
                  "Button",
                  {
                    props: {
                      type: "primary",
                      size: "small"
                    },
                    style: {
                      marginRight: "5px"
                    },
                    on: {
                      click: () => {
                        this.mShow(params.row);
                      }
                    }
                  },
                  "分红比例"
                ),
                h(
                  "Button",
                  {
                    props: {
                      type: "error",
                      size: "small"
                    },
                    on: {
                      click: () => {
                        this.mDelete(params.row);
                      }
                    }
                  },
                  "删除"
                )
              ]);
            } else {
              return h("div", [
                h(
                  "Button",
                  {
                    props: {
                      type: "error",
                      size: "small"
                    },
                    on: {
                      click: () => {
                        this.mDelete(params.row);
                      }
                    }
                  },
                  "删除"
                )
              ]);
            }
          }
        }
      ],
      dataList: []
    };
  },
  mounted() {
    this.mInit();
  },
  methods: {
    mInit() {
      this.mLoading(true);
      var params = Object.assign({}, this.searchVM);
      this.$http.post("/agentUser/agentliks.json", params).then(result => {
        if (result.code == 0) {
          this.dataList = !!result.rows ? result.rows : [];
        }
      });
    },
    mDelete(row) {
      this.mConfirm("确定要删除？", () => {
        this.mLoading(true);
        var params = Object.assign({}, row);
        this.$http
          .post("/agentUser/delAgentlinks.json", params)
          .then(result => {
            if (result.code == 0) {
              this.mAlert(result.message, "success");
              this.mInit(1);
            } else {
              this.mAlert(result.message, "error");
            }
          });
      });
    },
    mShow(row) {
      this.mWinOpen(
        "分红比例详情",
        () => import("./Commission"),
        {
          vmObj: {
            benefitAmountRebate: row.benefitAmountRebate,
            profitRebate: row.profitRebate,
            rakebackRebate: row.rakebackRebate
          }
        },
        300
      );
    }
  }
};
