import listMixin from "@/mixins/list";
import win from "@/mixins/window";
export default {
  name: "MessageList",
  mixins: [listMixin, win],
  data() {
    return {
      columns: [
        {
          title: "时间",
          key: "createTimeStr",
          align: "center",
          columnAlign: "center",
          isResize: true
        },
        {
          title: "消息类型",
          key: "msgTypeStr",
          align: "center",
          isResize: true
        },
        {
          title: "标题",
          key: "msgTitle",
          align: "center",
          isResize: true,
          width: 450
        },

        {
          title: "状态",
          key: "isRead",
          align: "center",
          render: (h, params) => {
            return h("span", {}, this.mIsReadLabel(params.row.isRead));
          }
        },
        {
          title: "查看",
          key: "id",
          isResize: true,
          align: "center",
          render: (h, params) => {
            return h(
              "i-button",
              {
                props: {
                  type: "warning"
                },
                on: {
                  click: () => {
                    this.mOpenInfo(params.row);
                  }
                }
              },
              "查看"
            );
          }
        }
      ],
      initData: {
        total: 0
      },
      searchVM: {
        msgType: 0, //消息类型
        startTime: "", //查询起始时间
        endTime: "", //查询结束时间
        rows: 10
      },
      dataList: [],
      agnetLevel: ""
    };
  },
  mounted() {
    /* setTimeout(() => {
      // 初始化日期选择控件
      $("#datepicker").datepicker({
        todayBtn: "linked", //显示今天按钮
        clearBtn: true, //显示清除按钮
        language: "zh-CN", //语言本地化
        orientation: "bottom auto", //显示位置
        autoclose: true, //自动关闭
        todayHighlight: true, //高亮今天
        format: "yyyy-mm-dd", //格式
        weekStart: 0 //星期的开始：周末
      });
    }, 500); */
    this.mLoading(true);
    this.$http.get("/memberUser/memberinfo.json").then(result => {
      if (result.code == 0) {
        this.agnetLevel = result.data.agnetLevel;
      }
    });
    this.mPullData();
  },
  methods: {
    select(v) {
      this.searchVM.startTime = v[0];
      this.searchVM.endTime = v[1];
    },
    mPullData() {
      this.mLoading(true);
      var params = Object.assign({}, this.searchVM);
      this.$http.post("/memberUser/getmessages.json", params).then(result => {
        if (result.code == 0) {
          this.initData.total = result.data.total;
          this.dataList = !!result.data.list ? result.data.list : [];
        }
      });
    },
    mOpenInfo(row) {
      this.mWinOpen("站内消息", () => import("./MsgInfo"), row, 500);
    },
    mIsReadLabel(state) {
      if (state == false) {
        return "未读";
      } else {
        return "已读";
      }
    }
  },
  created() {}
};
