import listMixin from "@/mixins/list";
import window from "../../mixins/window";
export default {
  mixins: [window, listMixin],

  data() {
    return {
      columns: [
        {
          title: "用户名",
          key: "username",
          align: "center"
        },
        {
          title: "用户类型",
          key: "memberTypeStr",
          align: "center"
        },
        {
          title: "团队人数",
          key: "teamNum",
          align: "center"
        },
        {
          title: "中心钱包",
          key: "centerAmount",
          render: (h, params) => {
            return h("span", {}, params.row.centerAmount / 100.0);
          },
          align: "center"
        },
        {
          title: "最后登陆",
          key: "createTimeStr",
          width: 200,
          align: "center"
        },
        {
          title: "操作",
          key: "transfer",
          width: 240,
          align: "center",
          render: (h, params) => {
            return h("div", [
              h(
                "Button",
                {
                  props: {
                    type: "primary",
                    size: "small"
                  },
                  style: {
                    marginRight: "5px"
                  },
                  on: {
                    click: () => {
                      // this.mOpen(params.rows.username);
                      this.$router.push({
                        path: "./bettingdetail_list",
                        query: { username: params.row.username }
                      });
                    }
                  }
                },
                "投注明细"
              ),
              h(
                "Button",
                {
                  props: {
                    type: "warning",
                    size: "small"
                  },
                  style: {
                    marginRight: "5px"
                  },
                  on: {
                    click: () => {
                      // this.remove(params.rows.username);
                      this.$router.push({
                        path: "./transactionsdetail_list",
                        query: { username: params.row.username }
                      });
                    }
                  }
                },
                "交易明细"
              ),
              h(
                "Button",
                {
                  props: {
                    type: "success",
                    size: "small"
                  },
                  on: {
                    click: () => {
                      this.mShow(params.row);
                    }
                  }
                },
                "分红比例"
              )
            ]);
          }
        }
      ],
      initData: {
        total: 0
      },
      searchVM: {
        memberType: "",
        username: "", //用户名查询
        orderBy: "id desc",
        rows: 20
      },
      dataList: []
    };
  },
  mounted() {
    this.mPullData();
  },
  methods: {
    mPullData() {
      this.mLoading(true);
      var params = Object.assign({}, this.searchVM);
      this.$http.post("/agentUser/teams.json", params).then(result => {
        if (result.code == 0) {
          this.dataList = !!result.rows ? result.rows : [];
          this.initData.total = result.total;
        }
      });
    },
    mShow(row) {
      this.mWinOpen(
        "分红比例详情",
        () => import("./Commission"),
        {
          vmObj: {
            benefitAmountRebate: row.benefitAmountRebate,
            profitRebate: row.profitRebate,
            rakebackRebate: row.rakebackRebate
          }
        },
        300
      );
    },
    mOpen(username) {
      this.$router.push({ path: "./bettingdetail_list" });
    }
  },
  created() {
    // this.mInit();
  }
};
