var _this;
let djs=null;//倒计时
let timerCheck=null;
export default {
    props: {
        paramData:Object,
        defult:{}
    },
    data(){
        return{
            isLoading:false,
            isShowQcode:false,
            isTimer:0,
            orderNo:'',
            qrcodePath:'',
            minute:'',
            second:'',
            isEnd:false,
        }
    },
    methods: {
        mInit() {
            let param={
                apiid:this.paramData.apiid,
                serviceType:this.paramData.serviceType,
                apitypeid:this.paramData.apitypeid,
                orderAmount:this.paramData.orderAmount,
            };
            this.isLoading = true;
            this.$http.post(this.paramData.url,param).then((result) => {
                this.mLoading(false);
                if(result.code=='0')
                {
                    this.isTimer=result.data.isTimer;
                    this.orderNo=result.data.orderNo;
                    this.qrcodePath=result.data.qrcoDepath;
                    this.isShowQcode=true;
                    if ( this.isTimer * 1 >0)
                    {
                        this.timer();
                    }
                    this.timerCheck= setTimeout(()=>{this.checkorder()}, 10000);
                    //console.log(result.data);
                }else{
                    this.mClose();
                }
                this.isLoading = false;
            }).catch(error=>{
                this.mClose();
            });
        },
        timer(){
             this.djs = window.setInterval( () =>{
                var d = 0,
                    h = 0;
                    //minute = 0,
                   // second = 0;//时间默认值
                if (this.isTimer > 0) {
                    this.minute = Math.floor(this.isTimer / 60) - (d * 24 * 60) - (h * 60);
                    this.second = Math.floor(this.isTimer) - (d * 24 * 60 * 60) - (h * 60 * 60) - (this.minute * 60);
                }
                this.isTimer--;
                if (this.isTimer <= 0) {
                    this.isEnd=true;
                    if (this.djs != null) {
                        window.clearInterval(this.djs);
                    }
                    if (this.timerCheck != null) {
                        clearTimeout(this.timerCheck);
                    }
                }
            }, 1000);
        },
        checkorder()
        {
             this.$http.post("/recharge/rechargeState.json?id="+this.orderNo,{}).then((result) => {
                 if(result.code==0)
                 {
                     if(result.data==1 || result.data==3)
                     {
                        this.mAlert("恭喜，充值成功！",()=>{this.mClose()},"success");
                        if (this.timerCheck != null) {
                            clearTimeout(this.timerCheck);
                        }
                     }
                     else if(result.data==2)
                     {
                        this.mAlert("抱歉，充值失败请联系客服！",()=>{this.mClose()},"error");
                        if (this.timerCheck != null) {
                            clearTimeout(this.timerCheck);
                        }
                     }
                     else {
                        this.timerCheck=setTimeout(()=>{this.checkorder()}, 2000);
                    }
                 }
                 else {
                    this.timerCheck=setTimeout(()=>{this.checkorder()}, 2000);
                }
             });
        },
        mClose() {
            this.$emit("closecallback");
        },
    },
    created() {
        _this = this;
        this.mInit();
    },
    destroyed() {
        _this = undefined;
        if (this.djs != null) {
            window.clearInterval(this.djs);
        }
        if (this.timerCheck != null) {
            clearTimeout(this.timerCheck);
        }
    }
}
